<script lang="ts" setup>
import { computed, onMounted } from 'vue';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { Article } from '~/types/article';
import { useAsyncData, useNuxtApp } from '#app';

const { data } = await useAsyncData('articles', async () => {
  const { $newtClient } = useNuxtApp();
  return await $newtClient.getContents<Article>({
    appUid: 'blog',
    modelUid: 'article',
    query: {
      select: ['_id', 'title', 'slug', 'body', 'coverImage', 'category', 'date']
    }
  });
});
const articles = data.value?.items ?? [];

const latestBlog = computed(() => {
  return articles ? articles[0] : null;
});

const nextBlogs = computed(() => {
  return articles ? articles.slice(1, 4) : [];
});
  
onMounted(() => {
  if (process.client) {
    const svg = document.querySelector<SVGSVGElement>('#svgTxt');

    if (svg) {
      const paths = svg.querySelectorAll<SVGPathElement>('path');

      paths.forEach(path => {
        const delayStr = path.getAttribute('data-delay');
        const delay = delayStr ? parseInt(delayStr) : 0;

        if (!isNaN(delay)) {
          setTimeout(() => {
            path.style.stroke = 'var(--sub-color)';
            path.style.strokeDasharray = path.getTotalLength().toString();
            path.style.strokeDashoffset = path.getTotalLength().toString();
            path.style.transition = 'stroke-dashoffset 2s ease-in-out';
            path.style.strokeDashoffset = '0';
          }, delay);
        }
      });
    }
    setTimeout(function() {
      const overlay = document.getElementById("overlay");
      const svgTxt = document.getElementById("svgTxt");
      if (overlay && svgTxt) {
        overlay.style.backgroundColor = "rgba(245, 243, 235, 0)";
        svgTxt.style.display = "none";
        setTimeout(function() {
          overlay.style.display = "none";
        }, 2000);
      }
    }, 3000);

    gsap.registerPlugin(ScrollTrigger);

    ScrollTrigger.defaults( {
      toggleActions:"play none none reverse"
    })

    gsap.to('.front-img-container', {
      scale: 120,
      ease: "ease",
      scrollTrigger: {
        trigger: '.move-section',
        scrub: 1,
        start: "top top",
        end: "bottom",
        pin: true
      }
    })

    gsap.to('.svg-txt2' , {
      autoAlpha: 0,
      duration: 1.5,
      scrollTrigger: {
      start: 1
      }
    })

    gsap.to('.txt-bottom', {
      autoAlpha: 0,
      letterSpacing: -10,
      duration: 1,
      scrollTrigger: {
        start: 2
      }
    })

    gsap.from('.txt-bottom', {
      letterSpacing: -10,
      opacity: 0,
      duration: 1
    })

    const tl = gsap.timeline();

    tl.from('.left-side', {
      y: 150,
      opacity: 0,
      stagger: {
        amount: .5
      },
      delay: 1.5
    })

    ScrollTrigger.create( {
      animation: tl,
      trigger: '.wrapper',
      start: "top top",
      end: "+=10",
      scrub: 1,
      pin: true,
    })
  }
});

const formatDate = (dateString: Date) => {
  const date = new Date(dateString);
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');

  return `${year}.${month}.${day}`;
};

useHead({
  title: 'Nuxt Base',
  meta: [
    { name: 'description', content: 'Nuxt Base is Web site by Nuxt.js' }
  ]
})
</script>
